#[derive(Properties, Clone, PartialEq)]
struct TitleProps {
    title: &'static str,
    id: Option<&'static str>,
}

#[function_component]
fn SectionTitle(props: &TitleProps) -> Html {
    html! {
        <div id={props.id.unwrap_or("")} class="h-[5vh] w-full max-w-screen-2xl mt-24 text-center mx-auto">
            <h1 class="font-black text-4xl text-zinc-800">
                {props.title}
            </h1>
        </div>
    }
}

#[function_component]
pub fn Contact() -> Html {
    let (store, _) = use_store::<SharedData>();
    let question_title = match store.language {
        Lang::CHN => "常見問題",
        Lang::JP => "よくある問題",
    };
    let questions = match store.language {
        Lang::CHN => &CHN_QUESTION_LIST,
        Lang::JP => &JP_QUESTION_LIST,
    };
    let contact_title = match store.language {
        Lang::CHN => "聯絡我們",
        Lang::JP => "お問い合わせ",
    };

    html! {
        <>
            <SectionTitle title={question_title}/>
            <ul class="mx-auto w-11/12">
                {for questions.into_iter().enumerate().map(|(i, (question, answer))| {
                    html!{
                        <Accordion nth={i+1} question={question.to_owned()} answer={answer.to_owned()} />
                    }
                })}
            </ul>
            <SectionTitle title={contact_title}  id="contact_form"/>
            <ContactForm />
        </>
    }
}
