#[component]
fn SectionTitle<F>(cx: Scope, title: F, #[prop(optional)] id: Option<&'static str>) -> impl IntoView
where
    F: Fn() -> &'static str + 'static,
{
    view! {cx,
        <div id=id class="h-[5vh] w-full max-w-screen-2xl mt-24 text-center mx-auto">
            <h1 class="font-bold text-3xl text-zinc-800">
                {title}
            </h1>
        </div>
    }
}

#[component]
pub fn Contact(cx: Scope) -> impl IntoView {
    let store = use_context::<ReadSignal<Store>>(cx).unwrap();
    view! {cx,
        <>
            <SectionTitle
                title=move || match store.with(|s| s.language) {
                    Lang::CHN => "常見問題",
                    Lang::JP => "よくある問題",
                }
            />
            <ul class="mx-auto w-11/12">
                {move || {
                    let questions = match store.with(|s| s.language) {
                        Lang::CHN => &CHN_QUESTION_LIST,
                        Lang::JP => &JP_QUESTION_LIST,
                    };
                    questions.into_iter().enumerate().map(|(i, (question, answer))| {
                        view!{cx,
                            <Accordion nth={i+1} question={*question} answer={*answer} />
                        }
                    }).collect_view(cx)
                }}
            </ul>
            <SectionTitle
                id="contact_form"
                title=move || match store.with(|s| s.language) {
                    Lang::CHN => "聯絡我們",
                    Lang::JP => "お問い合わせ",
                }
            />
            <ContactForm />
        </>
    }
}
